import styled from "styled-components";
import media from "styled-media-query";
import * as S from "../../components/CardPost/styled";

export const Wrapper = styled.div``;

export const ProfileContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  ${media.lessThan("medium")`
  display: flex;
  flex-direction: column;
  margin-top:3rem ;
  `}
  a {
    :hover {
      opacity: 0.7;
    }
    img {
      width: 10rem;
      height: 10rem;
      border-radius: 5rem;
    }
  }
  span {
    margin-left: 1rem;
    font-size: 2rem;
    color: ${(props) => props.theme.colors.terciary};
  }
`;
export const Test = styled.div`
  display: flex;
  flex-direction: column;
  align-self: center;
  align-items: center;
  margin-top: 3rem;
  h1 {
    position: relative;
    top: 50px;
    margin-bottom: 2rem;
    color: ${(props) => props.theme.colors.white};
    font-size: 2.5rem;
    ::before {
      content: " ";
      background-color: rebeccapurple;
      border: 3px solid ${(props) => props.theme.colors.primary};
      margin-right: 1rem;
    }
  }
`;

export const PostUser = styled.div`
  display: flex;
  gap: 1rem;
  flex-direction: row;
  max-height: 100%;
  margin-top: 6rem;
  width: 100%;
  justify-content: center;
  text-overflow: ellipsis;
  flex-wrap: wrap;
  a {
    width: 100%;
    display: flex;
    flex-direction: row;
    text-decoration: none;
  }
  ${S.Wrapper} {
    width: 40%;
    overflow: hidden;
    text-overflow: ellipsis;
    flex-wrap: nowrap;
  }
  ${S.PostTexTImportant} {
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: 60%;
    flex-wrap: wrap;
    flex-wrap: nowrap;
  }
`;

export const UserDesc = styled.div`
  display: flex;
  position: relative;
  top: 50px;
  left: -100px;

  margin-top: 1.5rem;
  width: 80%;
  word-break: break-all;

  ${media.lessThan("medium")`
  display: flex;
  flex-direction: column;
  position: initial;

  `}
  p {
    color: ${(props) => props.theme.colors.lightGray};
    opacity: 0.7;
  }
`;
