import styled from "styled-components";
import media from "styled-media-query";

export const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

export const Content = styled.div`
  padding-top: 5rem;
`;

export const ProfileEdit = styled.div`
  form {
    width: 100% !important;
    input {
      width: 35rem;
    }
  }
  ${media.lessThan("medium")`
form{
  background: red;
  width: 200px;
}

 `}
`;

export const AvatarInput = styled.div`
  padding: 1rem;

  margin-bottom: 1.2rem;

  img {
    width: 10rem;
    height: 10rem;
    border-radius: 5rem;
  }
  label {
    position: relative;

    width: 4.8rem;
    height: 4.8rem;
    input {
      display: none;
    }
    svg {
      color: white;
      font-size: 2.25rem;
      position: relative;
      left: -8px;
      bottom: 2px;
      cursor: pointer;
      :hover {
        opacity: 0.7;
      }
    }
  }
`;
