import styled from "styled-components";
import * as S from "../../components/CardPost/styled";
import media from "styled-media-query";

export const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  gap: 2rem;
  ${media.lessThan("medium")`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-top:2rem;

  `}
`;

export const CardContainer = styled.div`
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  background-color: red;
  flex-direction: column;

  ${media.lessThan("medium")`
  margin-top:2rem;
padding:1rem;
height: 100vh;
max-width: 100vw;
  `}

  gap: 2rem;
  width: 750px;
  ${S.Wrapper} {
    max-height: 100%;
    overflow: hidden;
    text-overflow: ellipsis;
    ${media.lessThan("medium")`
    display: flex;
    flex-direction: column;
    justify-content: center;

    `}
  }
  ${S.PostTexTImportant} {
    overflow: hidden;
    text-overflow: ellipsis;
    max-width: 100%;
    ${media.lessThan("medium")`
    max-width: 70%;
    overflow: hidden;
    text-overflow: ellipsis;
    display: flex;
    flex-direction: column;
    justify-content: center;


  `}
  }
`;

