export const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  gap: 2rem;
  ${media.lessThan("medium")`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-top:2rem;

