import React, { useCallback, useState } from "react";
import logo from "../../assets/profile.jpg";
import Menu from "../../components/Menu";
import { useAuth, AuthProvider } from "../../hooks/AuthContext";
import Button from "../../components/Button";
import * as S from "./styled";
import { Container } from "../../components/Container";
import api from "../../services/api";
import CardShowContainer from "../../components/CardPostShow";
import { useEffect } from "react";
import { AiFillCloseCircle } from "react-icons/ai";
import { IoIosArrowDown } from "react-icons/io";
import { Form } from "@unform/web";
type PostsProps = {
  id: string;
  id_user: string;
  post_text: string;
  user: {
    id: string;
    avatar?: string;
    name?: string;
    nickname?: string;
  };
};

const Home = () => {
  const [open, setIsOpen] = useState(false);
  const { user } = useAuth();

  const [post, setPost] = useState<PostsProps[]>([]);
  const [postText, setPostText] = useState("");
  const [page, setPage] = useState(5);
  const [loading, setLoading] = useState(false);

  console.log(loading);

  async function handlePost() {
    setLoading(true);
    const response = await api.get("/post", {
      params: {
        limit: page,
      },
    });
    setPost(response.data);
    setLoading(false);
  }

  useEffect(() => {
    handlePost();
  }, [page]);

  useEffect(() => {
    function infiniteScroll(e) {
      const scroll = window.scrollY;
      const heigth = document.body.offsetHeight - window.innerHeight;
      if (scroll > heigth * 0.75) {
        setPage(page + 3);
      }
    }
    window.addEventListener("wheel", infiniteScroll);
    window.addEventListener("scroll", infiniteScroll);

    return () => {
      window.removeEventListener("wheel", infiniteScroll);
      window.removeEventListener("scroll", infiniteScroll);
    };
  }, [page]);

  const handlePostSubmit = useCallback(async () => {
    await api.post("/post", {
      post_text: postText,
    });
    setPostText(" ");
    setIsOpen(false);
  }, [post, postText]);

  return (
    <S.Wrapper>
      <Menu userName={user.name} />
      <Container>
        <S.PostContainer>
          <S.ProfileContainer>
            <img src={user.avatar} />
          </S.ProfileContainer>
          <S.PostInput onClick={() => setIsOpen(true)}>
            <textarea
              name="post"
              placeholder={`Diga o que está pensando,${user.name}`}
              rows={1}
              cols={60}
              maxLength={150}
              value={postText}
              readOnly={true}
            ></textarea>
          </S.PostInput>
        </S.PostContainer>

        <S.CardContainer>
          {loading ? (
            <div>
              <h1>Carregando....</h1>{" "}
            </div>
          ) : (
            <div>Terminou</div>
          )}
        </S.CardContainer>

        <S.ModalContent open={open}>
          <S.PosterTextModal>
            <S.ProfileModalContainer>
              <img src={logo} />
              <span> {user.name} </span>
            </S.ProfileModalContainer>
            <S.PostModal>
              <Form onSubmit={handlePostSubmit}>
                <textarea
                  name=""
                  id=""
                  placeholder={`Diga o que está pensando,${user.name}`}
                  onChange={(e) => setPostText(e.target.value)}
                ></textarea>
                <Button type="submit">Postar</Button>
              </Form>
              <S.CloseModal onClick={() => setIsOpen(false)}>
                <AiFillCloseCircle size={30} />
              </S.CloseModal>
            </S.PostModal>
          </S.PosterTextModal>
        </S.ModalContent>
        <S.LoadPost>
          <S.LoadContent role="button" onClick={() => console.log("")}>
            <span>Carregar Mais</span>
            <IoIosArrowDown size={30} />
          </S.LoadContent>
        </S.LoadPost>
      </Container>
    </S.Wrapper>
  );
};

export default Home;
