import styled from "styled-components";

export const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;

  background-color: rgba(255, 232, 204, 0.5);
  overflow: auto;
`;

export const ModalContent = styled.div`
  background-color: #fefefe;
  width: 80%;
  margin: 15% auto;
  padding: 20px;
`;

export const CloseModal = styled.div``;
