import React, { useCallback, useState } from "react";

import logo from "../../assets/profile.jpg";
import Menu from "../../components/Menu";
import { useAuth, AuthProvider } from "../../hooks/AuthContext";

import * as S from "./styled";
import { Container } from "../../components/Container";
import api from "../../services/api";
import CardShowContainer from "../../components/CardPostShow";
import { useEffect } from "react";
import Input from "../../components/Input";
import { Form } from "@unform/web";
type PostsProps = {
  id: string;
  id_user: string;
  post_text: string;
  user: {
    id: string;
    avatar?: string;
    name?: string;
    nickname?: string;
  };
};

const Home = () => {
  const [open, setIsOpen] = useState(false);
  const { user } = useAuth();
  console.log(open);

  const [post, setPost] = useState<PostsProps[]>([]);
  const [postText, setPostText] = useState("");

  useEffect(() => {
    api
      .get("/post")
      .then((response) => setPost(response.data))
      .catch((err) => console.log(err));
  }, [post]);

  return (
    <S.Wrapper>
      <Menu userName={user.name} />
      <Container>
        <S.PostContainer>
          <S.ProfileContainer>
            <img src={logo} />
          </S.ProfileContainer>
          <S.PostInput onClick={() => setIsOpen(true)}>
            <textarea
              name="post"
              placeholder="No que estÃ¡  pensando,User"
              rows={3}
              cols={60}
              maxLength={150}
              onChange={(e) => setPostText(e.target.value)}
            ></textarea>
          </S.PostInput>
        </S.PostContainer>

        <S.CardContainer>
          {post.map((post, index) => (
            <CardShowContainer
              key={index}
              url_post={`/post/v1/${post.id}`}
              nickName={post.user.nickname}
              text_post={post.post_text}
              profile_image={`http://localhost:4001/files/${post.user.avatar}`}
            />
          ))}
        </S.CardContainer>
      </Container>
    </S.Wrapper>
  );
};

export default Home;
