import React, { useCallback, useState } from "react";
import Menu from "../../components/Menu";
import { useAuth, AuthProvider } from "../../hooks/AuthContext";
import { useRouteMatch } from "react-router-dom";
import * as S from "./styled";
import { Container } from "../../components/Container";
import api from "../../services/api";
import { useEffect } from "react";
import CardShowContainer from "../../components/CardPostShow";
import CardComments from "../../components/CardComments";
import { FaRegCommentAlt, FaRegSadTear } from "react-icons/fa";
import { Form } from "@unform/web";
import Button from "../../components/Button";

type PostsProps = {
  id: string;
  id_user: string;
  post_text: string;
  user: {
    id: string;
    avatar?: string;
    name?: string;
    nickname?: string;
  };
};
type CommentsProps = {
  id: string;
  text_comments: string;

  user: {
    id: string;
    avatar?: string;
    name?: string;
    nickname?: string;
  };
};

const DashBoard = () => {
  const { params } = useRouteMatch();
  const { user: userAuth } = useAuth();

  const [post, setPost] = useState({} as PostsProps);
  const [comments, setComments] = useState<CommentsProps[]>([]);

  useEffect(() => {
    api
      .get(`/post/v1/${params["id"]}`)
      .then((response) => setPost(response.data))
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    api
      .get(`/comments/post_comments/${params["id"]}`)
      .then((response) => setComments(response.data))
      .catch((e) => console.log(e));
  }, []);

  console.log(comments);

  return (
    <S.Wrapper>
      <Menu userName={userAuth.name} />
      <Container>
        <S.Content>
          {post && (
            <CardShowContainer
              key={post.id}
              text_post={post.post_text}
              name={post.user?.name}
              nickName={post.user?.nickname}
              profile_image={post.user?.avatar}
            />
          )}
          <S.CommentPostButton>
            <button>
              {" "}
              <FaRegCommentAlt />{" "}
            </button>
          </S.CommentPostButton>
          <h1>Comentarios</h1>
          {!comments.length ? (
            <S.NotComments>
              {" "}
              <FaRegSadTear />
              <h3>
                Sem Comentarios Ainda <br /> seja o Primeiro :)
              </h3>
            </S.NotComments>
          ) : (
            <S.CommentContainer>
              {comments.map((intem, index) => (
                <CardComments
                  key={index}
                  text_post={intem.text_comments}
                  profile_image={intem.user.avatar}
                  nickName={intem.user.nickname}
                  name={intem.user.name}
                />
              ))}
            </S.CommentContainer>
          )}
        </S.Content>
      </Container>
      <S.CommentModal>
        <S.ModalContent>
          <Form>
            <textarea
              name="comments"
              id="comments"
              cols={20}
              rows={2}
            ></textarea>
            <Button> Comentar</Button>
          </Form>
        </S.ModalContent>
      </S.CommentModal>
    </S.Wrapper>
  );
};

export default DashBoard;
