import React, { useState } from "react";
import * as S from "./styled";

import Logo from "../Logo/index";
import { FiMenu, FiUser } from "react-icons/fi";
import { AiFillCloseCircle } from "react-icons/ai";
import { Link, LinkProps } from "react-router-dom";

export type MenuProps = {
  userName?: string;
};

export type LinkPropsMenu = {
  Select: boolean;
} & LinkProps;

export default function Menu({ userName }: MenuProps): JSX.Element {
  const [isOpen, setIsOpen] = useState(false);
  const [selectd, setSelect] = useState(false);
  console.log(selectd);

  return (
    <S.Wrapper>
      <S.WrapperLogo>
        <Logo hiddenMobile={true} />
      </S.WrapperLogo>
      <S.MenuNavDesktop onClick={() => setSelect(true)}>
        <S.LinkMenu>
          <Link to="/">Explorar</Link>
        </S.LinkMenu>

        <S.LinkMenu>
          <Link to="/account">
            <FiUser />
            <span> Ol√°,{userName} </span>
          </Link>
        </S.LinkMenu>
      </S.MenuNavDesktop>

      <S.MenuNavMobile>
        <S.Menulink to="/account">
          <FiUser />
        </S.Menulink>
        <S.MenulinkIcon role="button" onClick={() => setIsOpen(true)}>
          <FiMenu />
        </S.MenulinkIcon>
      </S.MenuNavMobile>

      <S.MenuFullMobile isOpen={isOpen}>
        <S.CloseMenu onClick={() => setIsOpen(false)}>
          {" "}
          <AiFillCloseCircle />
        </S.CloseMenu>

        <S.Menulink to="/">Home</S.Menulink>
        <S.Menulink to="/account">Minha Conta</S.Menulink>
      </S.MenuFullMobile>
    </S.Wrapper>
  );
}
