import React, { useState, useEffect } from "react";
import { Container } from "../../components/Container";
import Menu from "../../components/Menu";
import * as S from "./styled";
import proffile from "../../assets/profile.jpg";
import { useAuth } from "../../hooks/AuthContext";
import { Link } from "react-router-dom";
import CardPostShow from "../../components/CardPostShow";
import api from "../../services/api";

interface UserProps {
  name: string;
  avatar: string;
  nickname: string;
}

const Account = () => {
  const { user } = useAuth();
  const [post, setPost] = useState([]);
  console.log(post);

  useEffect(() => {
    api
      .get(`/post/${user.id}`)
      .then((response) => setPost(response.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <S.Wrapper>
      <Menu userName={user.name} />
      <S.ProfileContainer>
        <img src={proffile} alt="" />
        <S.ButtonEditProfile>Edit Perfil</S.ButtonEditProfile>
        <S.ProfileData>
          <p>User</p>
          <span>NickName</span>
        </S.ProfileData>
      </S.ProfileContainer>

      <Container>
        {post.map((intem, index) => (
          <CardPostShow
            key={index}
            text_post={intem.post_text}
            url_post={`/post/v1/${intem.id}`}
          />
        ))}
      </Container>
    </S.Wrapper>
  );
};

export default Account;
