import React, { useCallback, useState } from "react";
import CardPost from "../../components/CardPost";
import logo from "../../assets/profile.jpg";
import Menu from "../../components/Menu";
import { useAuth, AuthProvider } from "../../hooks/AuthContext";
import { useRouteMatch } from "react-router-dom";
import * as S from "./styled";
import { Container } from "../../components/Container";
import api from "../../services/api";
import { useEffect } from "react";
import CardShowContainer from "../../components/CardPostShow";

const DashBoard = () => {
  const { params } = useRouteMatch();
  const { user } = useAuth();

  const [post, setPost] = useState([]);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    api
      .get(`/post/v1/${params["id"]}`)
      .then((response) => setPost(response.data))
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    api
      .get(`/comments/post_comments/${params["id"]}`)
      .then((response) => console.log(response.data))
      .catch((e) => console.log(e));
  }, []);

  console.log(post);
  return (
    <S.Wrapper>
      <Menu userName={user.name} />
      {post.map((intem, index) => (
        <CardShowContainer text_post={post.post_text} profile_image={user} />
      ))}
    </S.Wrapper>
  );
};

export default DashBoard;
