import styled from "styled-components";
import * as S from "../../components/CardPost/styled";
import media from "styled-media-query";
import theme from "../../styles/theme";

export const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  gap: 2rem;
  ${media.lessThan("medium")`
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-top:2rem;

  `}
`;

export const CardContainer = styled.div`
  display: flex;
  justify-content: flex-start;
  flex-direction: column;

  ${media.lessThan("medium")`
  margin-top:2rem;
padding:1rem;
height: 100vh;
max-width: 100vw;
  `}
  width: 750px;
`;

export const PostContainer = styled.div`
  display: flex;
  flex-direction: row;

  align-items: center;
`;

export const ProfileContainer = styled.div`
  img {
    width: 5rem;
    height: 5rem;
    border-radius: 2.5rem;
  }
  span {
    margin-left: 1rem;
  }
`;

export const PostInput = styled.div`
  height: 100%;
  margin-left: 1rem;

  width: 100%;
  textarea {
    text-overflow: ellipsis;
    overflow: hidden;
    word-break: break-all;
    width: 60%;
    padding: 1.25rem;
    text-align: left;
    resize: none;
    outline: none;
    border-radius: 1rem;
    background-color: ${(props) => props.theme.colors.lightGray};
    cursor: pointer;
    :hover {
      opacity: 0.7;
    }
    ::placeholder {
      font-size: 1.3rem;
      color: ${(props) => props.theme.colors.black};
      opacity: 0.5;
    }
  }
`;

export const ModalContent = styled.div`
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(60, 60, 60, 0.8);
  overflow: auto;
`;

export const ProfileModalContainer = styled.div`
  display: flex;
  background-color: yellow;
  img {
    width: 5rem;
    height: 5rem;
    border-radius: 2.5rem;
  }
`;

export const PosterTextModal = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;
  bottom: -18rem;
  left: 40rem;
  width: 40%;
  height: 60%;
  background-color: red;
`;

export const PostModalContainer = styled.div`
  background-color: yellow;
  display: flex;
  flex-direction: column;
  align-self: center;
`;
