import styled from "styled-components";
import * as S from "../../components/Input/styled";
import media from "styled-media-query";

export const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;

  background-color: rgba(255, 232, 204, 0.5);
  overflow: auto;
`;

export const ModalContent = styled.div`
  background-color: ${(props) => props.theme.colors.gray};
  width: 50%;
  margin: 5% auto;
  padding: 20px;
  ${media.lessThan("medium")`
  width: 100%;
  height:80%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 1rem

  `}
`;
type PropsPost = {
  isOpen: boolean;
};
export const CloseModal = styled.div<PropsPost>`
  position: relative;
  bottom: 250px;
  left: 600px;
  display: inline;
  cursor: pointer;
  ${media.lessThan("medium")`
  position: relative;
  bottom: 380px;
  left: 300px;

  `}
  svg {
    font-size: 1.8rem;
    color: ${(props) => props.theme.colors.white};
    ${media.lessThan("medium")`
    font-size: 3rem;

  `}
    :hover {
      opacity: 0.7;
    }
  }
`;

export const ProfileContainer = styled.div`
  display: flex;

  ${media.lessThan("medium")`
  display: flex;
  align-self: flex-start;
  position: absolute;
  top:0;
  margin: 5rem;
  `}
  padding: 1rem;
  align-self: center;
  align-items: center;
  p {
    padding-left: 1rem;
    font-size: 1.8rem;
    color: ${(props) => props.theme.colors.terciary};
  }
  img {
    width: 6rem;
    height: 6rem;
    border-radius: 3rem;
    ${media.lessThan("medium")`
    width: 8rem;
    height: 8rem;
    border-radius: 4rem;


  `}
  }
`;

export const FormPost = styled.div`
  display: flex;
  width: 100%;
  flex-direction: column;
  justify-content: center;

  align-items: center;
  ${S.Wrapper} {
    border: 0;
    width: 50%;
    padding: 1rem;
  }
  button {
    ${media.lessThan("medium")`
    width: 80%;
    margin-top:1rem

  `}
  }
  textarea {
    width: 100%;
    resize: none;
    border: none;
    border-bottom: 1px solid white;
    color: ${(props) => props.theme.colors.white};
    :focus {
      outline: none;
    }
    background-color: ${(props) => props.theme.colors.gray};
    ${media.lessThan("medium")`
  height:100%;

  `}
  }
`;
