import React, { useState } from "react";
import * as S from "./styled";
import Button from "../Button/index";
import Logo from "../Logo/index";
import { FiMenu, FiShoppingCart, FiUser } from "react-icons/fi";
import { AiFillCloseCircle } from "react-icons/ai";
import { Link } from "react-router-dom";

export type MenuProps = {
  userName?: string;
};

export default function Menu({ userName }: MenuProps) {
  const [isOpen, setIsOpen] = useState(false);

  const teste = () => {};
  return (
    <S.Wrapper>
      <S.WrapperLogo>
        <Logo hiddenMobile={true} />
      </S.WrapperLogo>
      <S.MenuNavDesktop>
        <Link to="/post">Postar</Link>
        <Link to="/">Home</Link>

        {!!userName && (
          <Link to="/account">
            <FiUser />
            <span> Ol√°,{userName} </span>
          </Link>
        )}
      </S.MenuNavDesktop>

      <S.MenuNavMobile>
        <S.Menulink to="/">
          <FiShoppingCart />
        </S.Menulink>
        <S.Menulink to="/account">
          <FiUser />
        </S.Menulink>
        <S.MenulinkIcon role="button" onClick={() => setIsOpen(true)}>
          <FiMenu />
        </S.MenulinkIcon>
      </S.MenuNavMobile>

      <S.MenuFullMobile isOpen={isOpen}>
        <S.CloseMenu onClick={() => setIsOpen(false)}>
          {" "}
          <AiFillCloseCircle />
        </S.CloseMenu>

        <S.Menulink to="/store">Loja</S.Menulink>
        <S.Menulink to="/">Explorar</S.Menulink>
        <S.Menulink to="/account">Minha Conta</S.Menulink>
        {!userName && (
          <S.MenuLogin>
            <S.Menulink to="/signin">Sign Up</S.Menulink>
            <span>Or</span>
            <Link to="/signup">
              {" "}
              <Button color="secondary">Log In Now</Button>
            </Link>
          </S.MenuLogin>
        )}
      </S.MenuFullMobile>
    </S.Wrapper>
  );
}
