import React, { useEffect, useState, ChangeEvent, useRef } from "react";
import api from "../../services/api";
import * as S from "./styled";
import { useAuth } from "../../hooks/AuthContext";
import { Container } from "../../components/Container";
import Input from "../../components/Input";
import Button from "../../components/Button";
import { Form } from "@unform/web";
import Menu from "../../components/Menu";
import { Link } from "react-router-dom";
import { FiCamera } from "react-icons/fi";
import { useCallback } from "react";
import { FormHandles } from "@unform/core";
import * as Yup from "yup";

type ProfileFormData = {
  name: string;
  email: string;
  old_password: string;
  password: string;
  password_confirmation: string;
};

const Edit = () => {
  const { user, updateUser } = useAuth();
  const formRef = useRef<FormHandles>(null);

  const handleAvatarChange = useCallback(
    (e: ChangeEvent<HTMLInputElement>) => {
      const data = new FormData();
      data.append("avatar", e.target.files[0]);
      console.log(data);
      api
        .put("users/avatar", data)
        .then((response) => {
          updateUser(response.data);
        })
        .catch((e) => console.log(e));
    },
    [updateUser]
  );

  const handleSubmit = useCallback(async (data: ProfileFormData) => {
    try {
      formRef.current.setErrors({});
      const schema = Yup.object().shape({
        name: Yup.string().required("Nome Obrigatorio"),
        nickname: Yup.string().required("Apelido Obrigatorio"),
        email: Yup.string().required().email("Digite um E-mail Valido"),
        old_password: Yup.string(),
        password: Yup.string().when("old_password", {
          is: (val) => !!val.length,
          then: Yup.string().required("Campo Obrigatorio"),
          otherwise: Yup.string(),
        }),
        password_confirmation: Yup.string()
          .when("old_password", {
            is: (val) => !!val.length,
            then: Yup.string().required("Campo Obrigatorio"),
            otherwise: Yup.string(),
          })
          .oneOf([Yup.ref("password"), null], "Senhas est√£o diferentes"),
      });

      await schema.validate(data, {
        abortEarly: false,
      });
      const {
        name,
        email,
        password,
        old_password,
        password_confirmation,
      } = data;

      const formData = {
        name,
        email,
        ...(old_password
          ? { old_password, password, password_confirmation }
          : {}),
      };
    } catch (error) {}
  }, []);

  return (
    <S.Wrapper>
      <Menu userName={user.name} />
      <Container>
        <S.Content>
          <S.AvatarInput>
            <img src={user.avatar} alt="" />
            <label htmlFor="avatar">
              <FiCamera />
              <input type="file" id="avatar" onChange={handleAvatarChange} />
            </label>
          </S.AvatarInput>
          <S.ProfileEdit>
            <Form
              initialData={{
                name: user.name,
                email: user.email,
                nickname: user.nickname,
              }}
              onSubmit={handleSubmit}
              sub
            >
              <Input name="name" type="text" placeholder="Name" />
              <Input name="nickname" type="text" placeholder="Apelido" />
              <Input name="email" type="email" placeholder="E-mail" />
              <Input name="password" type="password" placeholder="Nova Senha" />
              <Input
                name="old_password"
                type="password"
                placeholder="Senha Atual "
              />
              <Input
                name="password_confirmation"
                type="password"
                placeholder="Confirma Senha "
              />
              <Button style={{ width: "100%" }} type="submit">
                {" "}
                Salvar{" "}
              </Button>
            </Form>
          </S.ProfileEdit>
        </S.Content>
      </Container>
    </S.Wrapper>
  );
};

export default Edit;
