import React, { useState } from "react";
import * as S from "./styled";
import Button from "../Button/index";
import Logo from "../Logo/index";
import { FiMenu, FiShoppingCart, FiUser } from "react-icons/fi";
import { AiFillCloseCircle } from "react-icons/ai";
import { Link, useLocation } from "react-router-dom";

export type MenuProps = {
  userName?: string;
};

export default function Menu({ userName }: MenuProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [openAccount, setOpenAccount] = useState(false);
  const [openHome, setOpenHome] = useState(false);
  const location = useLocation();
  if (location.pathname === "/account") {
    return setOpenAccount(true);
  }
  if (location.pathname === "/home") {
    return setOpenHome(true);
  }

  const teste = () => {};
  return (
    <S.Wrapper>
      <S.WrapperLogo>
        <Logo hiddenMobile={true} />
      </S.WrapperLogo>
      <S.MenuNavDesktop>
        <Link to="/post">Postar</Link>
        <Link to="/">Explorar</Link>

        <Link to="/account">
          <FiUser />
          <span> Ol√°,{userName} </span>
        </Link>
      </S.MenuNavDesktop>

      <S.MenuNavMobile>
        <S.Menulink to="/account">
          <FiUser />
        </S.Menulink>
        <S.MenulinkIcon role="button" onClick={() => setIsOpen(true)}>
          <FiMenu />
        </S.MenulinkIcon>
      </S.MenuNavMobile>

      <S.MenuFullMobile isOpen={isOpen}>
        <S.CloseMenu onClick={() => setIsOpen(false)}>
          {" "}
          <AiFillCloseCircle />
        </S.CloseMenu>

        <S.Menulink to="/post">Postar</S.Menulink>
        <S.Menulink to="/">Home</S.Menulink>
        <S.Menulink to="/account">Minha Conta</S.Menulink>
      </S.MenuFullMobile>
    </S.Wrapper>
  );
}
