import styled from "styled-components";
import * as S from "../../components/Input/styled";
export const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  position: fixed;
  z-index: 1;
  left: 0;
  top: 0;

  background-color: rgba(255, 232, 204, 0.5);
  overflow: auto;
`;

export const ModalContent = styled.div`
  background-color: ${(props) => props.theme.colors.gray};
  width: 50%;
  margin: 5% auto;
  padding: 20px;
`;

export const CloseModal = styled.div``;

export const ProfileContainer = styled.div`
  display: flex;

  padding: 1rem;
  align-self: center;
  align-items: center;
  p {
    padding-left: 1rem;
    font-size: 1.8rem;
    color: ${(props) => props.theme.colors.terciary};
  }
  img {
    width: 6rem;
    height: 6rem;
    border-radius: 3rem;
  }
`;

export const FormPost = styled.div`
  display: flex;
  width: 100%;
  flex-direction: column;
  justify-content: center;

  align-items: center;
  ${S.Wrapper} {
    border: 0;
    width: 50%;
  }

  textarea {
    width: 100%;
    resize: none;
    padding: 1rem;
    border: none;
  }
`;
